import Arrow from "../../assets/images/icon-arrow-down.svg";
import "./CustomSelectBox.css";
import Logo from "../../assets/images/logo.svg";
import React, { useState, useEffect, useRef } from "react";

const CustomSelectBox = ({ darkMode, fontFamily, onFontFamilyChange }) => {
  const [selectedOption, setSelectedOption] = useState("San Serif");
  const options = ["San Serif", "Serif", "Mono"];
  const [isOptionsOpen, setIsOptionsOpen] = useState(false);
  const ref = useRef(null);

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
    switch (event.target.value) {
      case "San Serif":
        onFontFamilyChange("sans-serif");
        break;
      case "Serif":
        onFontFamilyChange("serif");
        break;
      case "Mono":
        onFontFamilyChange("monospace");
        break;
      default:
        onFontFamilyChange("");
        break;
    }
  };

  const handleArrowClick = () => {
    setIsOptionsOpen(!isOptionsOpen);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (ref.current && !ref.current.contains(event.target)) {
        setIsOptionsOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [ref]);

  return (
    <div id="header-container" 
    className={darkMode ? "dark-mode" : ""}
    >
      <img src={Logo} alt="Logo" />
      <div className={`custom-select-box ${darkMode ? "dark-mode" : ""}`}>
        <div className={`select-box ${isOptionsOpen ? "open" : ""}`} ref={ref}>
          <div className="selected-option" onClick={handleArrowClick}>
            {selectedOption}
            <img id="arrow" src={Arrow} alt="Arrow down" />
          </div>
          {isOptionsOpen && (
            <div className="options">
              {options.map((option) => (
                <div
                  key={option}
                  className={`option ${
                    selectedOption === option ? "selected" : ""
                  }`}
                  onClick={() => {
                    handleOptionChange({ target: { value: option } });
                    handleArrowClick();
                  }}
                >
                  {option}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomSelectBox;
