import { useState } from "react";

const Main = ({ darkMode, selectedFont }) => {
  const [wordData, setWordData] = useState(null);
  const [error, setError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const searchWord = e.target.elements.search.value;

    fetch(`https://api.dictionaryapi.dev/api/v2/entries/en/${searchWord}`)
      .then(async (res) => {
        if (!res.ok) {
          throw new Error();
        }
        setError(false);
        setErrorMessage("");
        return res.json();
      })
      .then((data) => {
        setWordData(data);
      })
      .catch(() => {
        setError(true);
        setErrorMessage("No result");
      });
  };

  return (
    <div id="main-container" className={`${darkMode ? "dark-mode" : ""}`}>
      <div style={{ fontFamily: selectedFont }}>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            id="search-input"
            name="search"
            style={{
              backgroundImage: `url(${SearchIcon})`,
              backgroundRepeat: "no-repeat",
              backgroundPosition: " 95% 50%",
              fontFamily: selectedFont, // add fontFamily style
            }}
          />
        </form>
        {wordData && (
          <div>
            <div id="word-audio-div">
              <div>
                <h1 id="word">{wordData[0].word}</h1>
                <p id="phonetic">{wordData[0].phonetics[0].text}</p>
              </div>
              <img
                id="play-icon"
                src={PlayIcon}
                alt="Play Icon"
                onClick={() => {
                  const audioObj = wordData[0].phonetics.find((p) => p.audio);
                  if (audioObj) {
                    const audio = new Audio(audioObj.audio);
                    audio.play();
                  }
                }}
              />
            </div>
            <div id="definition-div">
              <p id="definition">{wordData[0].meanings[0].partOfSpeech}</p>
              <hr />
            </div>
            <p id="meaning">Meaning</p>
            <ul id="meanings">
              {wordData[0].meanings[0].definitions.map((definition, index) => (
                <li key={index}>{definition.definition}</li>
              ))}
            </ul>
            <div id="synonymos-div">
              <h3 id="synonymos">Synonyms</h3>
              {wordData[0].meanings
                .map((meaning) =>
                  meaning.definitions.find((def) => def.synonyms.length > 0)
                )
                .filter((def) => def)
                .map((def) => (
                  <p id="synonymos-example">{def.synonyms[0]}</p>
                ))}
            </div>
          </div>
        )}
        <div id="definition-div">
          <p id="definition">verb</p>
          <hr />
        </div>
        <p id="meaning">Meaning</p>
        <ul id="meanings">
          <li>To type on a computer keyboard.</li>
          <li id="example">
            “Keyboarding is the part of this job I hate the most.”
          </li>
        </ul>
        {error && <p>{errorMessage}</p>}
        <hr id="bottom-hr" />
        <div id="link-div">
          <a id="source" href="/">
            Source
          </a>
          <div id="source-div">
            <a id="link" href="/">
              https://en.wiktionary.org/wiki/keyboard
            </a>
            <img src={NewWindow} alt="New Window" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Main;
