import Arrow from "../../assets/images/icon-arrow-down.svg";
import "./CustomSelectBox.css";
import Logo from "../../assets/images/logo.svg";
import React, { useState, useEffect, useRef } from "react";

const CustomSelectBox = ({ darkMode }) => {
  const [selectedOption, setSelectedOption] = useState("San Serif");
  const [selectedFontOption, setSelectedFontOption] = useState(null);/*this*/
  const options = [ "San Serif",  "Serif", "Mono"];
  const [isOptionsOpen, setIsOptionsOpen] = useState(false);
  const ref = useRef(null);

  const handleArrowClick = () => {
    setIsOptionsOpen(!isOptionsOpen);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (ref.current && !ref.current.contains(event.target)) {
        setIsOptionsOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [ref]);

  return (
    <div id="header-container" 
    className={darkMode ? "dark-mode" : ""}
    >
      <img src={Logo} alt="Logo" />
      <div className={`custom-select-box ${darkMode ? "dark-mode" : ""}`}>
        <div className={`select-box ${isOptionsOpen ? "open" : ""}`} ref={ref}>
        <div className={`selected-option ${selectedFontOption === "Inter" ? "inter-font" : ""} ${selectedFontOption === "Lora" ? "lora-font" : ""}`} onClick={handleArrowClick}>
            {selectedOption}
            <img id="arrow" src={Arrow} alt="Arrow down" />
          </div>
          {isOptionsOpen && (
            <div className={`options ${selectedFontOption === "San Serif" || selectedOption === "Serif" ? "inter-font lora-font" : ""}`}>
              {options.map((option) => (
                <div
                  key={option}
                  className={`option ${
                    selectedOption === option ? "selected" : ""
                  }`}
                  data-font={option === "Sans Serif" ? "Inter" : option === "Serif" ? "Lora" : ""}
                  onClick={() => {
                    setSelectedOption(option);
                    setSelectedFontOption(option === "San Serif" ? "Inter" : option === "Serif" ? "Lora" : null);
                    setIsOptionsOpen(false);
                  }}
                >
                  {option}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomSelectBox;
