import Logo from '../assets/images/logo.svg';
import Arrow from '../assets/images/icon-arrow-down.svg';
import { useState } from 'react';
const Dictionary = () => {

    const [selectedFonts, setSelectedFonts] = useState([]);

    const handleFontChange = (event) => {
        const selectedOptions = Array.from(event.target.selectedOptions, (option) => option.value);
        setSelectedFonts(selectedOptions);
      };

    return (
        <div id="container">
            <header>
               <img src={Logo} alt="Logo"/> 

    <select multiple onChange={handleFontChange} style={{display:"none"}}>
      <option value='blue'>Sans Serif</option>
      <option value='green'>Serif</option>
      <option value='red'>Mono</option>
      <img src={Arrow} alt="Arrow down"/>
      </select>
      <div>
        <p>Selected fonts: {selectedFonts.join(', ')}</p>
      </div>

            </header>


        </div>
    )
}

export default Dictionary
