import Arrow from "../../assets/images/icon-arrow-down.svg";
import "./CustomSelectBox.css";
import Logo from "../../assets/images/logo.svg";
import React, { useState, useEffect, useRef } from "react";

const CustomSelectBox = ({ darkMode }) => {
  const [selectedOption, setSelectedOption] = useState("San Serif");
  const [selectedFontFamily, setSelectedFontFamily] = useState("Inter");
  const options = [ "San Serif",  "Serif", "Mono"];
  const [isOptionsOpen, setIsOptionsOpen] = useState(false);
  const ref = useRef(null);

  const handleArrowClick = () => {
    setIsOptionsOpen(!isOptionsOpen);
    setSelectedFontFamily(selectedOption === "San Serif" ? "Inter" : selectedOption === "Serif" ? "Lora" : "Inconsolata");
    options.map((option) => {
      if (option === selectedOption) {
        ref.current.querySelector(`[data-font="${selectedFontFamily}"]`).classList.add('selected');
      } else {
        ref.current.querySelector(`[data-font="${option === "San Serif" ? "Inter" : option === "Serif" ? "Lora" : "Inconsolata"}"]`).classList.remove('selected');
      }
    });
  };


  useEffect(() => {
    const handleClickOutside = (event) => {
      if (ref.current && !ref.current.contains(event.target)) {
        setIsOptionsOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [ref]);

  return (
    <div id="header-container" 
    className={darkMode ? "dark-mode" : ""}
    >
      <img src={Logo} alt="Logo" />
      <div className={`custom-select-box ${darkMode ? "dark-mode" : ""}`}>
        <div className={`select-box ${isOptionsOpen ? "open" : ""}`} ref={ref}>
        <div className={`selected-option ${selectedOption === "San Serif" ? "inter-font" : selectedOption === "Serif" ? "lora-font" : selectedOption === "Mono" ? "inconsolata-font" : ""}`} onClick={handleArrowClick}>
            {selectedOption}
            <img id="arrow" src={Arrow} alt="Arrow down" />
          </div>
          {isOptionsOpen && (
            <div className="options">
              {options.map((option) => (
                <div
                  key={option}
                  className={`option ${
                    selectedOption === option ? "selected" : ""
                  }`}
                  data-font={option === "San Serif" ? "Inter" : option === "Serif" ? "Lora" : option === "Mono" ? "Inconsolata" : null}
                  onClick={() => {
                    setSelectedOption(option);
                    setIsOptionsOpen(false);
                    setSelectedFontFamily(option === "San Serif" ? "Inter" : option === "Serif" ? "Lora" : "Inconsolata");
                    options.map((option) => {
                      if (option === selectedOption) {
                        ref.current.querySelector(`[data-font="${selectedFontFamily}"]`).classList.add('selected');
                      } else {
                        ref.current.querySelector(`[data-font="${option === "San Serif" ? "Inter" : option === "Serif" ? "Lora" : "Inconsolata"}"]`).classList.remove('selected');
                      }
                    });
                  }}

                >
                  {option}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomSelectBox;
