const CustomSelectBox = ({ darkMode }) => {
    const [selectedOption, setSelectedOption] = useState("San Serif");
    const [fontFamily, setFontFamily] = useState("Inter");
    const options = ["San Serif", "Serif", "Mono"];
    const [isOptionsOpen, setIsOptionsOpen] = useState(false);
    const ref = useRef(null);

    const handleArrowClick = () => {
      setIsOptionsOpen(!isOptionsOpen);
    };

    const handleOptionClick = (option) => {
      setSelectedOption(option);
      setIsOptionsOpen(false);
      setFontFamily(option === "San Serif" ? "Inter" : option === "Serif" ? "Lora" : "Inconsolata");
    };

    useEffect(() => {
        if (ref.current) {
          const selectedElement = ref.current.querySelector(`[data-font="${fontFamily}"]`);
          if (selectedElement) {
            selectedElement.classList.add("selected");
          }
        }
      }, [ref, fontFamily]);


    useEffect(() => {
      const handleClickOutside = (event) => {
        if (ref.current && !ref.current.contains(event.target)) {
          setIsOptionsOpen(false);
        }
      };
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [ref]);

    return (
      <div
        id="header-container"
        className={darkMode ? "dark-mode" : ""}
      >
        <img src={Logo} alt="Logo" />
        <div className={`custom-select-box ${darkMode ? "dark-mode" : ""}`}>
          <div className={`select-box ${isOptionsOpen ? "open" : ""}`} ref={ref}>
            <div
              className={`selected-option ${selectedOption.toLowerCase()}-font`}
              onClick={handleArrowClick}
            >
              {selectedOption}
              <img id="arrow" src={Arrow} alt="Arrow down" />
            </div>
            {isOptionsOpen && (
              <div className="options" style={{ fontFamily: fontFamily }}>
                {options.map((option) => (
                  <div
                    key={option}
                    className={`option ${
                      selectedOption === option ? "selected" : ""
                    }`}
                    data-font={option === "San Serif" ? "Inter" : option === "Serif" ? "Lora" : "Inconsolata"}
                    onClick={() => handleOptionClick(option)}
                  >
                    {option}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };

  export default CustomSelectBox;
